import React from "react";
import ReactDOM from "react-dom";

const Header = (props) => <h1>{props.course}</h1>;

const Part = (props) => {
  return (
    <p>
      {props.part} {props.exercise}
    </p>
  );
};

const Content = (props) => {
  return (
    <div>
      <Part part={props.data[0].name} exercise={props.data[0].exercise} />
      <Part part={props.data[1].name} exercise={props.data[1].exercise} />
      <Part part={props.data[2].name} exercise={props.data[2].exercise} />
    </div>
  );
};

const Total = (props) => <p>Number of exercises {props.sum}</p>;

const App = () => {
  const course = "Half stack application development";

  const part1 = "Fundamentals of React";
  const exercises1 = 10;
  const part2 = "Using props to pass data";
  const exercises2 = 7;
  const part3 = "State of a component";
  const exercises3 = 14;

  let data = [
    { name: "Fundamentals of React", exercise: 10 },
    { name: "Using props to pass data", exercise: 7 },
    { name: "State of a component", exercise: 14 },
  ];

  const sum = exercises1 + exercises2 + exercises3;

  return (
    <div>
      <Header course={course} />
      <Content data={data} />
      <Total sum={sum} />
    </div>
  );
};

ReactDOM.render(<App />, document.getElementById("root"));
